from solid_simulation import Particles, Springs, RunSimulationimport matplotlib.pyplot as pltnr_x, nr_y, nr_z, hight = 2, 2, 2, 10layer_x, layer_y, layer_z = 2, 2, 2;nr_particles = layer_x * layer_y * layer_zp = Particles()for z in range(nr_x):    for y in range(nr_y):        for x in range(nr_z):            p.set_particle_pos((x, y, z + hight))            p.set_particle_mass(10)            nr_springs = 1springs = []for particle in range(nr_particles-1):        if (particle+1) % nr_x != 0:        springs.append(Springs(particle, particle + 1, 10))        run = RunSimulation()run.move()run.draw()