# Build the solid# Spring and node is objects from force_vector import ForceVector as Vecimport matplotlib.pyplot as pltclass Particles:        def __init__(self, mass=1):        self.mass = []        self.pos = []        self.vel = []                def set_particle_pos(self, pos):        self.pos.append(Vec(*pos))    def set_particle_mass(self, mass):        self.mass.append(mass)            def set_particle_vel(self, vel):        self.vel.append(Vec(vel))    class Springs:        def __init__(self, spring_from=0, spring_to=0, length=0):        self.spring_to = spring_to        self.spring_from = spring_from        self.length = length                            class RunSimulation:    def __init__(self, size=400):                self.size = size        self.fig, self.ax = plt.subplots(            1,            1,            subplot_kw={"projection": "3d"},            figsize=(self.size / 50, self.size / 50),        )                self.ax.view_init(0, 0)        self.fig.tight_layout()            def move(self):        self.position = (            1,            2,            3,        )            def draw(self):          self.ax.plot(            *self.position,            marker="o",            markersize=10,        )                 self.ax.set_xlim((-self.size / 2, self.size / 2))        self.ax.set_ylim((-self.size / 2, self.size / 2))        self.ax.set_zlim((-self.size / 2, self.size / 2))                plt.pause(0.001)        #self.ax.clear()